                                    <div class="article-2x-description">
                                        <?= $dataArray[$i]['author']; ?>
                                        <span>-</span>
                                        <time datetime="<?= date('Y-m-d', strtotime($dataArray[$i]['date'])) . $dataArray[$i]['time']; ?>"><?= $dataArray[$i]['date']; ?></time>
