            const link = 'https://profitproton.com/tools/business-idea-generator'; // Replace this with the actual link you want to copy

            // Create a temporary textarea element to copy the link
            const tempTextarea = document.createElement('textarea');
            tempTextarea.value = link;
            document.body.appendChild(tempTextarea);

            // Select the text inside the textarea
            tempTextarea.select();
            tempTextarea.setSelectionRange(0, 99999); // For mobile devices

            // Copy the selected text to clipboard
            document.execCommand('copy');

            // Remove the temporary textarea
            document.body.removeChild(tempTextarea);

            // Show the message box
            const messageBox = document.getElementById('messageBox');
            messageBox.classList.add('show');

            // Hide the message box after a short delay (e.g., 2 seconds)
            setTimeout(() => {
                messageBox.classList.remove('show');
            }, 2000);
        }
    </script>

    <script>
        function getRandomInt(min, max) {
            return Math.floor(Math.random() * (max - min + 1)) + min;
        }

        const categoryFileMap = {
            'Content Creation': 'content_creation.xls',
            'E-commerce': 'ecommerce.xls',
            // Add more categories and file names as needed
        };

        const allCategories = 'All Categories';
        let ideaQueue = [];
        let lastDisplayedIdea = null;

        function limitInputLength(input, maxLength) {
            if (input.value.length > maxLength) {
                input.value = input.value.slice(0, maxLength);
            }
        }

        function generateIdea() {
            const categorySelect = document.getElementById('idea-categories-select');
            const budgetInput = document.getElementById('idea-budget-input');
            const ideaSpan = document.querySelector('.svg-text');

            const selectedCategory = categorySelect.value;
            const budget = parseInt(budgetInput.value);

            if (selectedCategory === allCategories) {
                const allIdeas = [];
                const filesToFetch = Object.values(categoryFileMap);

                Promise.all(
                        filesToFetch.map((fileName) => fetchIdeasFromFile(fileName, budget))
                    )
                    .then((allCategoryIdeas) => {
                        allCategoryIdeas.forEach((ideas) => allIdeas.push(...ideas));
                        if (allIdeas.length === 0) {
                            ideaSpan.textContent = 'No idea found within your budget for all categories';
                        } else {
                            ideaQueue = allIdeas;
                            displayRandomIdea();
                        }
                    })
                    .catch((error) => {
                        ideaSpan.textContent = 'An error occurred while fetching the data. Please try again.';
                        console.error(error);
                    });
            } else {
                if (isNaN(budget) || budget <= 0) {
                    ideaSpan.textContent = 'Please enter a valid budget';
                    return;
                }

                const fileName = categoryFileMap[selectedCategory];
                if (!fileName) {
                    ideaSpan.textContent = 'No XLS file found for the selected category';
                    return;
                }

                if (ideaQueue.length === 0) {
                    fetchIdeasFromFile(fileName, budget)
                        .then((ideas) => {
                            if (ideas.length === 0) {
                                ideaSpan.textContent = 'No idea found within your budget for the selected category';
                            } else {
                                ideaQueue = ideas;
                                displayRandomIdea();
                            }
                        })
                        .catch((error) => {
                            ideaSpan.textContent = 'An error occurred while fetching the data. Please try again.';
                            console.error(error);
                        });
                } else {
                    displayRandomIdea();
                }
            }
        }

        function fetchIdeasFromFile(fileName, budget) {
            return fetch('/tempid/' + fileName)
                .then((response) => response.text())
                .then((data) => {
                    const lines = data.split('\n');
                    const ideas = [];

                    lines.forEach((line) => {
                        const [idea, ideaBudget] = line.split('\t');
                        const parsedBudget = parseInt(ideaBudget);
                        if (!isNaN(parsedBudget) && parsedBudget <= budget) {
                            ideas.push(idea);
                        }
                    });

                    return ideas;
                });
        }

        function displayRandomIdea() {
            const ideaSpan = document.querySelector('.svg-text');
            if (ideaQueue.length > 1) {
                let randomIndex = getRandomInt(0, ideaQueue.length - 1);
                while (ideaQueue[randomIndex] === lastDisplayedIdea) {
                    randomIndex = getRandomInt(0, ideaQueue.length - 1);
                }
                lastDisplayedIdea = ideaQueue[randomIndex];
                ideaSpan.textContent = lastDisplayedIdea;
                ideaQueue.splice(randomIndex, 1);
            } else if (ideaQueue.length === 1) {
                lastDisplayedIdea = ideaQueue[0];
                ideaSpan.textContent = lastDisplayedIdea;
                ideaQueue = [];
            } else {
                ideaSpan.textContent = 'No more ideas for the selected category. Click Generate again to start over.';
            }
        }

        function checkLength(input) {
            limitInputLength(input, 10); // Change 10 to the desired max length
            // You can add any other validation logic here if needed for the budget input.
        }

        const generateButton = document.querySelector('.generate-idea');
        generateButton.addEventListener('click', generateIdea);

        const budgetInput = document.getElementById('idea-budget-input');
        budgetInput.addEventListener('input', function() {
            checkLength(this);
        });
    </script>







